(1, 1, 'verify_email', 'Verify Your Email Address', '<p>Hi <b>{username}</b>,</p><p>We just need to verify your email address before you can access to your dashboard.</p><p>Verify your email address, {verification_link}.</p><p>Thank you.<br>{website_title}</p>'),
(2, 1, 'reset_password', 'Recover Password of Your Account', '<p>Hi {customer_name},</p><p>We have received a request to reset your password. If you did not make the request, just ignore this email. Otherwise, you can reset your password using this below link.</p><p>{password_reset_link}</p><p>Thanks,<br>{website_title}</p>'),
(3, 1, 'course_enrolment', 'Enrolment Confirmation', '<p>Hi <span style=\'font-weight:600;\'>{customer_name}</span>,</p><p>You have successfully enrol in the following course.</p><p>Order Id: #{order_id}<br>Course: {title} </p><p>Also, we have attached an invoice in this mail.</p><p>Best regards.<br>{website_title}</p>'),
(4, 1, 'course_enrolment_approved', 'Approval of Course Enrolment', '<p>Hi <span style=\'font-weight:600;\'>{customer_name}</span>,</p><p>Your payment is completed, and we have approved your enrolment for the following course.</p><p>Order Id: #{order_id}<br /></p><p>Course: {title}</p><p>Also, we have attached an invoice in this mail.</p><p>Best regards.<br />{website_title}</p>'),
(5, 1, 'course_enrolment_rejected', 'Rejection of Course Enrolment', '<p>Hi <span style=\'font-weight:600;\'>{customer_name}</span>,</p><p>Your payment is not completed, thus we have rejected your enrolment for the following course.</p><p>Order Id: #{order_id}<br /></p><p>Course: {title}</p><p>For further information, please do not hesitate to contact us.<br />{website_title}</p>'),
